#Read from address_matches file
file = open('address_matches.csv', 'r')
content = file.read()

addresses_match = content.split("\n")
numOfmatches = addresses_match[2]
numOfmatches = numOfmatches[9:]
addresses_match = addresses_match[3:]
file.close()
    
    
amt_profit_total = 0
address_split = []
for i in addresses_match:

    address_split = i.split("&")
    tok0 = address_split[0]
    if len(tok0) > 0:
        tok1 = address_split[1]

        #Check how much of Tok1 is needed to pay back flashloan and if enough reserve to take flashloan
        #Check if there is a WMATIC pair to swap profit into after
        address_wmatic = '0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270'
        address_pair_uni = factory_contract_uni.functions.getPair(tok1, address_wmatic).call()
        if address_pair_uni != '0x0000000000000000000000000000000000000000':
            address_pair_uni = factory_contract_uni.functions.getPair(tok0, tok1).call()
            pair_contract_uni = web3.eth.contract(address=address_pair_uni, abi=abi_uni)
            flash_reserves_uni = pair_contract_uni.functions.getReserves().call()[0]

            address_pair_sushi = factory_contract_sushi.functions.getPair(tok0, tok1).call()
            pair_contract_sushi = web3.eth.contract(address=address_pair_sushi, abi=abi_sushi)
            liquidity_sushi = pair_contract_sushi.functions.getReserves().call()[0]

            #Calculate trade amount according to uniswap reserve and sushiswap liquidity
            start = int(min(flash_reserves_uni, liquidity_sushi) / 2)
            ret_amt = int(((start * 3) / 997) + start) + 1

            #print tok0 and tok1 to tell where error is happening
            #print(tok0 + ',' + tok1)
            #print(ret_amt)
        
            if ret_amt > 10000:

                amt_required = router_contract_uni.functions.getAmountsIn(ret_amt, [tok1, tok0]).call()
                if amt_required[0] > 10000:
                    #Check how much of Tok1 will be received after sushiswap and if there are enough reserves to do that 
                    amt_from_sushi = router_contract_sushi.functions.getAmountsOut(start, [tok0, tok1]).call()
                
                    if (amt_from_sushi[1] > (amt_required[0] * 1.04)):
                        #convert profit back to wmatic
                        amt_profit = amt_from_sushi[1] - amt_required[0]
                        amt_profit_conv = router_contract_uni.functions.getAmountsOut(amt_profit, [tok1, address_wmatic]).call()[1]
                        amt_profit_total = amt_profit_total + amt_profit_conv
                        print(tok0 + ',' + tok1)
                        print('flash loan amount: ', start, 'need to return: ', ret_amt)
                        print('need this after sushiswap to profit: ', amt_required[0])
                        print('sushiswap will give: ', amt_from_sushi[1])
                        print('profit in token: ', amt_profit, ' profit in wmatic: ', amt_profit_conv)
                        print('total profit for all: ', amt_profit_total)
                    
print('Done')




